import React, { Component } from "react";

// import RenderBarChart from './chart'

class Converter extends Component {
  state = {
    currencies: ["EUR"],
    base: "USD",
    amount: 0,
    convertTo: "EUR",
    result: "",
    date: "",
    response: null
  };

  componentDidMount() {


    fetch(`https://api.exchangeratesapi.io/latest?base=${this.state.base}`)
      .then(res => res.json())
      .then(data => {
        const rates = data.rates;

        let currencyArray = [];
        for (let v in rates) {
          currencyArray.push(v);
        }
        this.setState({ currencies: currencyArray, response: data.rates });
        console.table(this.state.response)
      })

  }

  handleSelect = e => {
    this.setState(
      {
        [e.target.name]: e.target.value,
        result: null
      },

      this.calculate
    );
  };

  handleInput = e => {
    this.setState(
      {
        amount: e.target.value,
        result: null,
        date: null
      },
      this.calculate
    );
  };

  calculate = () => {
    const amount = this.state.amount;
    if (amount === isNaN || amount < 0) {
      alert("No negatives allowed");
      this.setState({ amount: 0 });
    } else {
      console.log(amount);

      fetch(`https://api.exchangeratesapi.io/latest?base=${this.state.base}`)
        .then(res => res.json())
        .then(data => {
          const date = data.date;
          const result = (data.rates[this.state.convertTo] * amount).toFixed(4);
          this.setState({
            result,
            date
          });
        });
    }
  };
  handleSwap = e => {
    const base = this.state.base;
    const convertTo = this.state.convertTo;
    e.preventDefault();
    this.setState(
      {
        convertTo: base,
        base: convertTo,
        result: null
      },
      this.calculate
    );
  };

  render() {
    const { currencies, base, amount, convertTo, result, date } = this.state;
    return (
      <div className="converter">
        <div className="container my-5">
          <div className="row">
            <div className="col-lg-6 mx-auto">
              <div className="card card-body">
                <h5>
                  {amount} {base} is equivalent to
              </h5>
                <h2>
                  {amount === ""
                    ? "0"
                    : result === null
                      ? "Calculating..."
                      : result}{" "}
                  {convertTo}
                </h2>
                <p>As of {amount === "" ? "/ / /" : date === null ? "" : date}</p>
                <div className="row">
                  <div className="col-lg-10">
                    <form className="form-inline mb-4">
                      <input
                        type="number"
                        value={amount}
                        onChange={this.handleInput}
                        className="form-control form-control-lg mx-3"
                        id="amount"
                      />
                      <select
                        name="base"
                        value={base}
                        onChange={this.handleSelect}
                        className="form-control form-control-lg"
                        id="currency"
                      >
                        {currencies.map(currency => (
                          <option key={currency} value={currency}>
                            {currency}
                          </option>
                        ))}
                      </select>
                    </form>

                    <form className="form-inline mb-4">
                      <input
                        disabled={true}
                        value={
                          amount === ""
                            ? "0"
                            : result === null
                              ? "Calculating..."
                              : result
                        }
                        className="form-control form-control-lg mx-3"
                        id="amount"
                      />
                      <select
                        name="convertTo"
                        value={convertTo}
                        onChange={this.handleSelect}
                        className="form-control form-control-lg"
                        id="currency"
                      >
                        {currencies.map(currency => (
                          <option key={currency} value={currency}>
                            {currency}
                          </option>
                        ))}
                      </select>
                    </form>
                  </div>

                  <div className="col-lg-2 align-self-center">
                    <h1 onClick={this.handleSwap} className="swap">
                      &#8595;&#8593;
                  </h1>
                  </div>
                </div>
              </div>
            </div>

          </div>
          {/* <RenderBarChart responseData={this.state.response}/> */}
        </div>
      </div>
    );
  }
}

export default Converter;
